mapper.select:
select s.* ,c.name clazzName from student s left join clazz c
on s.clazz_id = c.id


        s.name like concat('%',#{name},'%')


        and s.clazz_id = #{clazzId}


        and s.degree = #{degree}


order by s.create_time desc

mapper.update:
UPDATE student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, degree = #{degree}, graduation_date = #{graduationDate}, clazz_id = #{clazzId}, update_time = #{updateTime}, violation_count = #{violationCount}, violation_score = #{violationScore},
WHERE id = #{id}

UPDATE student
violation_count = violation_count + 1, violation_score = violation_score + #{score},
WHERE id = #{id}
